import { useEffect, useState } from "react";
import Header from "./components/header";
import Sidebar from "./components/sidebar";
import Canvas from "./components/canvas";
import "./styles/canvasx.css";

export default function App() {
  const [blocks, setBlocks] = useState([]);
  const [loaded, setLoaded] = useState(false);

  // LOAD
  useEffect(() => {
    const saved = localStorage.getItem("canvasx_blocks");
    if (saved) setBlocks(JSON.parse(saved));
    setLoaded(true);
  }, []);

  // AUTOSAVE
  useEffect(() => {
    if (!loaded) return;
    localStorage.setItem("canvasx_blocks", JSON.stringify(blocks));
  }, [blocks, loaded]);

  // EXPORT
  const exportProject = () => {
    const body = blocks
      .map(
        (b) => `
        <section style="padding:40px;border-bottom:1px solid #ddd">
          <h2>${b.type}</h2>
          <p>${b.content}</p>
        </section>`
      )
      .join("");

    const html = `
<!DOCTYPE html>
<html>
<head>
<meta charset="UTF-8" />
<title>CanvasX Export</title>
<style>
body{font-family:Arial;margin:0;padding:0}
</style>
</head>
<body>${body}</body>
</html>`;

    const blob = new Blob([html], { type: "text/html" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "canvasx-export.html";
    a.click();
  };

  return (
    <div className="app">
      <Header onExport={exportProject} />
      <div className="builder">
        <Sidebar />
        <Canvas blocks={blocks} setBlocks={setBlocks} />
      </div>
    </div>
  );
}
