import { useEffect, useState } from "react";
import Header from "./components/header";
import Sidebar from "./components/sidebar";
import Canvas from "./components/canvas";
import "./styles/canvasx.css";

function App() {
  const [blocks, setBlocks] = useState([]);

  // ✅ LOAD saved project (SAFE)
  useEffect(() => {
    try {
      const saved = localStorage.getItem("canvasx");
      if (saved) {
        const parsed = JSON.parse(saved);

        // validate data shape
        if (Array.isArray(parsed)) {
          const cleanBlocks = parsed.filter(
            (b) => b && typeof b === "object" && "id" in b && "content" in b
          );
          setBlocks(cleanBlocks);
        }
      }
    } catch (err) {
      console.error("Failed to load CanvasX data", err);
    }
  }, []);

  // ✅ AUTOSAVE
  useEffect(() => {
    localStorage.setItem("canvasx", JSON.stringify(blocks));
  }, [blocks]);

  return (
    <div className="app">
      <Header />
      <div className="builder">
        <Sidebar />
        <Canvas blocks={blocks} setBlocks={setBlocks} />
      </div>
    </div>
  );
}

export default App;
