import React, { useState } from "react";

function Canvas({ blocks = [], setBlocks }) {
  const [dragIndex, setDragIndex] = useState(null);

  const onDragOver = (e) => {
    e.preventDefault();
  };

  const onDrop = (e, dropIndex = null) => {
    e.preventDefault();

    const newType = e.dataTransfer.getData("blockType");

    // ðŸ”µ ADD NEW BLOCK (from sidebar)
    if (newType) {
      setBlocks((prev) => [
        ...prev,
        { id: Date.now(), content: newType },
      ]);
      return;
    }

    // ðŸŸ¡ REORDER BLOCK
    if (dragIndex === null || dropIndex === null) return;

    setBlocks((prev) => {
      const updated = [...prev];
      const [moved] = updated.splice(dragIndex, 1);
      updated.splice(dropIndex, 0, moved);
      return updated;
    });

    setDragIndex(null);
  };

  // ðŸ—‘ DELETE
  const deleteBlock = (id) => {
    setBlocks((prev) => prev.filter((b) => b.id !== id));
  };

  // âœ EDIT
  const editBlock = (id, value) => {
    setBlocks((prev) =>
      prev.map((b) =>
        b.id === id ? { ...b, content: value } : b
      )
    );
  };

  return (
    <main className="canvas" onDragOver={onDragOver} onDrop={onDrop}>
      {blocks.length === 0 && (
        <p className="canvas-hint">
          Drag blocks from the left and drop them here
        </p>
      )}

      {blocks.map((block, index) => (
        <div
          key={block.id}
          className="canvas-block"
          draggable
          onDragStart={() => setDragIndex(index)}
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => onDrop(e, index)}
        >
          <input
            value={block.content}
            onChange={(e) =>
              editBlock(block.id, e.target.value)
            }
          />

          <button
            className="delete-btn"
            onClick={() => deleteBlock(block.id)}
          >
            âœ•
          </button>
        </div>
      ))}
    </main>
  );
}

export default Canvas;
