const blocks = [
  "Hero Section",
  "Bio Section",
  "Gallery Section",
  "Publications Section",
  "Contact Section",
  "Footer",
];

function Sidebar() {
  const handleDragStart = (e, block) => {
    e.dataTransfer.setData("blockType", block);
  };

  return (
    <div className="sidebar">
      <h2>Block Library</h2>

      {blocks.map((block) => (
        <div
          key={block}
          className="block"
          draggable
          onDragStart={(e) => handleDragStart(e, block)}
        >
          {block}
        </div>
      ))}
    </div>
  );
}

export default Sidebar;
