import { useEffect, useState } from "react";
import interact from "interactjs";

export default function Canvas({ blocks, setBlocks }) {
  const [dragIndex, setDragIndex] = useState(null);

  /* ===============================
     ENABLE RESIZE USING interact.js
     =============================== */
  useEffect(() => {
    interact(".canvas-block").resizable({
      edges: { right: true, bottom: true },
      modifiers: [
        interact.modifiers.restrictSize({
          min: { width: 200, height: 60 },
          max: { width: 900, height: 400 },
        }),
      ],
      listeners: {
        move(event) {
          const target = event.target;

          const width =
            (parseFloat(target.style.width) || target.offsetWidth) +
            event.deltaRect.width;

          const height =
            (parseFloat(target.style.height) || target.offsetHeight) +
            event.deltaRect.height;

          target.style.width = width + "px";
          target.style.height = height + "px";
        },
        end(event) {
          const id = Number(event.target.dataset.id);

          setBlocks((prev) =>
            prev.map((b) =>
              b.id === id
                ? {
                    ...b,
                    width: event.target.offsetWidth,
                    height: event.target.offsetHeight,
                  }
                : b
            )
          );
        },
      },
    });

    return () => interact(".canvas-block").unset();
  }, [setBlocks]);

  /* ===============================
     DROP HANDLER
     =============================== */
  const onDrop = (e, index = null) => {
    e.preventDefault();
    const type = e.dataTransfer.getData("blockType");

    // ADD NEW BLOCK
    if (type) {
      setBlocks([
        ...blocks,
        {
          id: Date.now(),
          type,
          content: "",
          width: 600,
          height: 120,
        },
      ]);
      return;
    }

    // REORDER BLOCK
    if (dragIndex === null) return;

    const updated = [...blocks];
    const moved = updated.splice(dragIndex, 1)[0];
    updated.splice(index, 0, moved);

    setBlocks(updated);
    setDragIndex(null);
  };

  return (
    <main
      className="canvas"
      onDragOver={(e) => e.preventDefault()}
      onDrop={onDrop}
    >
      {blocks.length === 0 && (
        <p className="canvas-hint">
          Drag blocks from the left and drop them here.
        </p>
      )}

      {blocks.map((b, i) => (
        <div
          key={b.id}
          data-id={b.id}
          className="canvas-block"
          draggable
          onDragStart={() => setDragIndex(i)}
          onDrop={(e) => onDrop(e, i)}
          onDragOver={(e) => e.preventDefault()}
          style={{
            width: b.width || "600px",
            height: b.height || "120px",
          }}
        >
          <input
            value={b.content}
            onChange={(e) =>
              setBlocks(
                blocks.map((x) =>
                  x.id === b.id
                    ? { ...x, content: e.target.value }
                    : x
                )
              )
            }
          />

          <button
            onClick={() =>
              setBlocks(blocks.filter((x) => x.id !== b.id))
            }
          >
            âœ•
          </button>
        </div>
      ))}
    </main>
  );
}
