import { useEffect, useState } from "react";
import Header from "./components/header";
import Sidebar from "./components/sidebar";
import Canvas from "./components/canvas";
import "./styles/canvasx.css";

function App() {
  const [blocks, setBlocks] = useState([]);

  // LOAD (on refresh)
  useEffect(() => {
    const saved = localStorage.getItem("canvasx_blocks");
    if (saved) {
      setBlocks(JSON.parse(saved));
    }
  }, []);

  // AUTOSAVE
  useEffect(() => {
    localStorage.setItem("canvasx_blocks", JSON.stringify(blocks));
    console.log("AUTOSAVED:", blocks);
  }, [blocks]);

  // EXPORT JSON
  const exportJSON = () => {
    const blob = new Blob([JSON.stringify(blocks, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "canvasx-project.json";
    a.click();
  };

  // EXPORT HTML
  const exportHTML = () => {
    const html = `
<!DOCTYPE html>
<html>
<head>
<title>CanvasX Export</title>
<style>
body { font-family: Arial; padding: 40px; }
.section { margin-bottom: 30px; }
</style>
</head>
<body>
${blocks
  .map(
    (b) => `<div class="section"><h2>${b.content}</h2></div>`
  )
  .join("")}
</body>
</html>
`;

    const blob = new Blob([html], { type: "text/html" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "canvasx-export.html";
    a.click();
  };

  return (
    <div className="app">
      <Header onExportJSON={exportJSON} onExportHTML={exportHTML} />
      <div className="builder">
        <Sidebar />
        <Canvas blocks={blocks} setBlocks={setBlocks} />
      </div>
    </div>
  );
}

export default App;
