import { useRef } from "react";

export default function Canvas({ blocks, addBlock, setBlocks }) {
  const dragIndex = useRef(null);

  function handleDrop(e) {
    e.preventDefault();
    const type = e.dataTransfer.getData("type");
    if (type) addBlock(type);
  }

  function onDragStart(index) {
    dragIndex.current = index;
  }

  function onDragOver(index) {
    if (dragIndex.current === index) return;

    const updated = [...blocks];
    const dragged = updated[dragIndex.current];
    updated.splice(dragIndex.current, 1);
    updated.splice(index, 0, dragged);

    dragIndex.current = index;
    setBlocks(updated);
  }

  return (
    <div className="canvas" onDragOver={(e) => e.preventDefault()} onDrop={handleDrop}>
      {blocks.map((block, i) => (
        <div
          key={i}
          className="canvas-block"
          draggable
          onDragStart={() => onDragStart(i)}
          onDragOver={() => onDragOver(i)}
        >
          {block}
        </div>
      ))}
    </div>
  );
}
