function Header({ blocks }) {
  const exportHTML = () => {
    const html = `
<!DOCTYPE html>
<html>
<head>
<title>CanvasX Export</title>
<style>
body { font-family: Arial; padding: 40px; }
section { margin-bottom: 40px; }
</style>
</head>
<body>
${blocks
  .map(
    (b) => `<section><h2>${b.content}</h2></section>`
  )
  .join("")}
</body>
</html>
    `;

    const blob = new Blob([html], { type: "text/html" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "canvasx.html";
    a.click();
  };

  return (
    <header className="header">
      <div>
        <h1>CanvasX</h1>
        <p>Visual Web Builder Studio</p>
      </div>

      <div className="header-actions">
        <span onClick={exportHTML}>Export</span>
        <span>Help</span>
      </div>
    </header>
  );
}

export default Header;
