import React from "react";

const BLOCKS = [
  "Hero Section",
  "Bio Section",
  "Gallery Section",
  "Publications Section",
  "Contact Section",
  "Footer",
];

function Sidebar() {
  const onDragStart = (e, type) => {
    e.dataTransfer.setData("blockType", type);
    e.dataTransfer.effectAllowed = "copy";
  };

  return (
    <aside className="sidebar">
      <h3>Block Library</h3>

      <button className="export-btn">Export Project</button>

      {BLOCKS.map((block) => (
        <div
          key={block}
          className="block"
          draggable
          onDragStart={(e) => onDragStart(e, block)}
        >
          {block}
        </div>
      ))}
    </aside>
  );
}

export default Sidebar;
