import React from "react";

function Header() {
  const exportProject = () => {
    const blocks = JSON.parse(localStorage.getItem("canvasx")) || [];

    if (blocks.length === 0) {
      alert("Nothing to export!");
      return;
    }

    const html = `
<!DOCTYPE html>
<html>
<head>
  <title>CanvasX Export</title>
  <style>
    body { font-family: Arial; padding: 40px; }
    section { margin-bottom: 40px; border-bottom: 1px solid #ccc; }
  </style>
</head>
<body>
  ${blocks
    .map(
      (block) => `
    <section>
      <h2>${block}</h2>
      <p>Generated from CanvasX</p>
    </section>
  `
    )
    .join("")}
</body>
</html>
`;

    const blob = new Blob([html], { type: "text/html" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "canvasx-export.html";
    link.click();
  };

  return (
    <header className="header">
      <div>
        <h1 className="logo">CanvasX</h1>
        <p className="subtitle">Visual Web Builder Studio</p>
      </div>

      <div className="header-actions">
        <button onClick={exportProject} className="export-btn">
          Export
        </button>
      </div>
    </header>
  );
}

export default Header;
