import React, { useState } from "react";

function Canvas({ blocks, setBlocks }) {
  const [dragIndex, setDragIndex] = useState(null);

  // Allow drop on canvas
  const onDragOver = (e) => {
    e.preventDefault();
  };

  // Handle drop (add new block OR reorder)
  const onDrop = (e, dropIndex = null) => {
    e.preventDefault();

    const blockType = e.dataTransfer.getData("blockType");

    // ðŸ”µ ADD NEW BLOCK (from sidebar)
    if (blockType) {
      setBlocks((prev) => [
        ...prev,
        {
          id: Date.now(),
          content: blockType,
        },
      ]);
      return;
    }

    // ðŸŸ¡ REORDER EXISTING BLOCK
    if (dragIndex === null || dropIndex === null) return;

    const updated = [...blocks];
    const movedItem = updated.splice(dragIndex, 1)[0];
    updated.splice(dropIndex, 0, movedItem);

    setBlocks(updated);
    setDragIndex(null);
  };

  // âŒ DELETE BLOCK
  const deleteBlock = (id) => {
    setBlocks(blocks.filter((b) => b.id !== id));
  };

  // âœï¸ EDIT BLOCK
  const editBlock = (id, value) => {
    setBlocks(
      blocks.map((b) =>
        b.id === id ? { ...b, content: value } : b
      )
    );
  };

  return (
    <main
      className="canvas"
      onDragOver={onDragOver}
      onDrop={onDrop}
    >
      {/* EMPTY CANVAS HINT */}
      {blocks.length === 0 && (
        <p className="canvas-hint">
          Drag blocks from the left and drop them here
        </p>
      )}

      {/* BLOCKS */}
      {blocks.map((block, index) => (
        <div
          key={block.id}
          className="canvas-block"
          draggable
          onDragStart={() => setDragIndex(index)}
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => onDrop(e, index)}
        >
          <input
            value={block.content}
            onChange={(e) =>
              editBlock(block.id, e.target.value)
            }
          />

          <button
            className="delete-btn"
            onClick={() => deleteBlock(block.id)}
          >
            âœ•
          </button>
        </div>
      ))}
    </main>
  );
}

export default Canvas;
