import { useState } from "react";

function Canvas({ blocks, setBlocks }) {
  const [dragIndex, setDragIndex] = useState(null);

  const onDrop = (e, index = null) => {
    e.preventDefault();
    const type = e.dataTransfer.getData("blockType");

    // ADD new block
    if (type) {
      setBlocks(prev => [
        ...prev,
        { id: Date.now(), type, content: type }
      ]);
      return;
    }

    // REORDER block
    if (dragIndex === null || index === null) return;
    const updated = [...blocks];
    const moved = updated.splice(dragIndex, 1)[0];
    updated.splice(index, 0, moved);
    setBlocks(updated);
    setDragIndex(null);
  };

  return (
    <main
      className="canvas"
      onDragOver={e => e.preventDefault()}
      onDrop={onDrop}
    >
      {blocks.length === 0 && (
        <p className="canvas-hint">
          Drag blocks from the left and drop them here
        </p>
      )}

      {blocks.map((block, index) => (
        <div
          key={block.id}
          className="canvas-block"
          draggable
          onDragStart={() => setDragIndex(index)}
          onDrop={e => onDrop(e, index)}
          onDragOver={e => e.preventDefault()}
        >
          {/* EDIT */}
          <input
            value={block.content}
            onChange={e =>
              setBlocks(
                blocks.map(b =>
                  b.id === block.id
                    ? { ...b, content: e.target.value }
                    : b
                )
              )
            }
          />

          {/* DELETE */}
          <button
            className="delete-btn"
            onClick={() =>
              setBlocks(blocks.filter(b => b.id !== block.id))
            }
          >
            âœ•
          </button>
        </div>
      ))}
    </main>
  );
}

export default Canvas;
