import { useEffect } from "react";
import interact from "interactjs";

function Sidebar() {
  useEffect(() => {
    interact(".block").draggable({
      inertia: true,
      listeners: {
        move(event) {
          const target = event.target;

          const x =
            (parseFloat(target.getAttribute("data-x")) || 0) + event.dx;
          const y =
            (parseFloat(target.getAttribute("data-y")) || 0) + event.dy;

          target.style.transform = `translate(${x}px, ${y}px)`;
          target.setAttribute("data-x", x);
          target.setAttribute("data-y", y);
        },
        end(event) {
          event.target.style.transform = "translate(0,0)";
          event.target.removeAttribute("data-x");
          event.target.removeAttribute("data-y");
        },
      },
    });
  }, []);

  return (
    <aside className="sidebar">
      <h3>Block Library</h3>

      <button className="export-btn">Export Project</button>

      <div className="block" data-type="Hero">Hero Section</div>
      <div className="block" data-type="Bio">Bio Section</div>
      <div className="block" data-type="Gallery">Gallery</div>
      <div className="block" data-type="Publications">Publications</div>
      <div className="block" data-type="Contact">Contact Section</div>
      <div className="block" data-type="Footer">Footer</div>
    </aside>
  );
}

export default Sidebar;
