import { useRef } from "react";

function Canvas({ blocks, setBlocks, addBlock }) {
  const dragIndex = useRef(null);

  // DROP FROM SIDEBAR
  const onDrop = (e) => {
    e.preventDefault();
    const type = e.dataTransfer.getData("blockType");
    if (type) addBlock(type);
  };

  // REORDER
  const onDragStart = (index) => {
    dragIndex.current = index;
  };

  const onDragOver = (index) => {
    if (dragIndex.current === index) return;

    const updated = [...blocks];
    const moved = updated.splice(dragIndex.current, 1)[0];
    updated.splice(index, 0, moved);

    dragIndex.current = index;
    setBlocks(updated);
  };

  // DELETE
  const deleteBlock = (id) => {
    setBlocks(blocks.filter((b) => b.id !== id));
  };

  // EDIT
  const editBlock = (id, value) => {
    setBlocks(
      blocks.map((b) =>
        b.id === id ? { ...b, content: value } : b
      )
    );
  };

  return (
    <main
      className="canvas"
      onDragOver={(e) => e.preventDefault()}
      onDrop={onDrop}
    >
      {blocks.length === 0 && (
        <p className="canvas-hint">
          Drag blocks from the left and drop them here
        </p>
      )}

      {blocks.map((block, index) => (
        <div
          key={block.id}
          className="canvas-block"
          draggable
          onDragStart={() => onDragStart(index)}
          onDragOver={() => onDragOver(index)}
        >
          <input
            value={block.content}
            onChange={(e) => editBlock(block.id, e.target.value)}
          />

          <button onClick={() => deleteBlock(block.id)}>âœ•</button>
        </div>
      ))}
    </main>
  );
}

export default Canvas;
