import { useEffect, useState } from "react";
import Header from "./components/header";
import Sidebar from "./components/sidebar";
import Canvas from "./components/canvas";
import "./styles/canvasx.css";

function App() {
  const [blocks, setBlocks] = useState([]);

  // 游댯 LOAD (runs once on refresh)
  useEffect(() => {
    const saved = localStorage.getItem("canvasx");
    if (saved) {
      try {
        setBlocks(JSON.parse(saved));
      } catch {}
    }
  }, []);

  // 游리 DEBUG (logs every change)
  useEffect(() => {
    console.log("BLOCKS STATE CHANGED:", blocks);
  }, [blocks]);

  // 游릭 AUTOSAVE
  useEffect(() => {
    localStorage.setItem("canvasx", JSON.stringify(blocks));
  }, [blocks]);

  return (
    <div className="app">
      <Header />
      <div className="builder">
        <Sidebar />
        <Canvas blocks={blocks} setBlocks={setBlocks} />
      </div>
    </div>
  );
}

export default App;
