import { useState, useEffect } from "react";
import Header from "./components/header";
import Sidebar from "./components/sidebar";
import Canvas from "./components/canvas";
import "./styles/canvasx.css";

function App() {
  const [blocks, setBlocks] = useState([]);

  // ✅ load saved blocks
  useEffect(() => {
    const saved = localStorage.getItem("canvasx");
    if (saved) setBlocks(JSON.parse(saved));
  }, []);

  // ✅ save blocks
  useEffect(() => {
    localStorage.setItem("canvasx", JSON.stringify(blocks));
  }, [blocks]);

  // ✅ add block ONLY on drop
  const addBlock = (type) => {
    setBlocks((prev) => [...prev, type]);
  };

  return (
    <div className="app">
      <Header />
      <div className="builder">
        <Sidebar />
        <Canvas blocks={blocks} setBlocks={setBlocks} addBlock={addBlock} />
      </div>
    </div>
  );
}

export default App;
