function Sidebar() {
  const blocks = [
    "Hero Section",
    "Bio Section",
    "Gallery Section",
    "Publications Section",
    "Contact Section",
    "Footer",
  ];

  const onDragStart = (e, type) => {
    e.dataTransfer.setData("blockType", type);
  };

  return (
    <aside className="sidebar">
      <h3>Block Library</h3>

      {blocks.map((block) => (
        <div
          key={block}
          className="sidebar-block"
          draggable
          onDragStart={(e) => onDragStart(e, block)}
        >
          {block}
        </div>
      ))}
    </aside>
  );
}

export default Sidebar;
