import React, { useState } from "react";

function Canvas({ blocks, setBlocks, addBlock }) {
  const [dragIndex, setDragIndex] = useState(null);

  // Allow drop
  const handleDragOver = (e) => {
    e.preventDefault();
  };

  // Drop from sidebar OR reorder inside canvas
  const handleDrop = (e, dropIndex) => {
    e.preventDefault();

    const blockType = e.dataTransfer.getData("blockType");

    // ðŸŸ¡ FROM SIDEBAR (new block)
    if (blockType) {
      addBlock(blockType);
      return;
    }

    // ðŸ”µ REORDER inside canvas
    if (dragIndex === null || dragIndex === dropIndex) return;

    const updated = [...blocks];
    const movedItem = updated.splice(dragIndex, 1)[0];
    updated.splice(dropIndex, 0, movedItem);

    setBlocks(updated);
    setDragIndex(null);
  };

  return (
    <div className="canvas" onDragOver={handleDragOver}>
      {blocks.length === 0 && (
        <p className="canvas-placeholder">
          Drag blocks from the left and drop them here
        </p>
      )}

      {blocks.map((block, index) => (
        <div
          key={index}
          className="canvas-block"
          draggable
          onDragStart={() => setDragIndex(index)}
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => handleDrop(e, index)}
        >
          {block}
        </div>
      ))}
    </div>
  );
}

export default Canvas;
