import { useEffect } from "react";
import interact from "interactjs";

function Canvas({ blocks, addBlock }) {
  useEffect(() => {
    interact(".canvas").dropzone({
      accept: ".block",
      overlap: 0.5,

      ondrop(event) {
        const type = event.relatedTarget.getAttribute("data-type");
        addBlock(type);
      },
    });
  }, [addBlock]);

  return (
    <main className="canvas">
      {blocks.length === 0 && (
        <p className="canvas-text">
          Drag blocks from the left and drop them here
        </p>
      )}

      {blocks.map((block, index) => (
        <div key={index} className="canvas-block">
          {block.type} Section
        </div>
      ))}
    </main>
  );
}

export default Canvas;
